// Simple code node to extract knowledge tracking from AI response
// Place this node after your AI Agent and before "Map fields for further processing"

const aiOutput = $json.output || '';

// Initialize defaults
let knowledgeData = {
  knowledge_sources_used: 0,
  knowledge_sources: [],
  search_queries_made: []
};

let cleanResponse = aiOutput;

try {
  // Look for our simple tracking format
  const trackingMatch = aiOutput.match(/KNOWLEDGE_SOURCES_USED:(.*?)END_TRACKING/s);

  if (trackingMatch && trackingMatch[1]) {
    const trackingContent = trackingMatch[1].trim();

    // Extract sources used count
    const sourcesUsedMatch = trackingContent.match(/KNOWLEDGE_SOURCES_USED:\s*(\d+)/);
    if (sourcesUsedMatch) {
      knowledgeData.knowledge_sources_used = parseInt(sourcesUsedMatch[1]);
    }

    // Extract source titles
    const sourcesMatch = trackingContent.match(/SOURCES:\s*([^\n]+)/);
    if (sourcesMatch && sourcesMatch[1]) {
      const sourcesTitles = sourcesMatch[1].split(',').map(s => s.trim()).filter(s => s);
      knowledgeData.knowledge_sources = sourcesTitles.map((title, index) => ({
        id: `source_${index + 1}`,
        title: title,
        category: "Knowledge Base",
        relevance_score: 0.8,
        used_in_response: true
      }));
    }

    // Extract search queries
    const searchesMatch = trackingContent.match(/SEARCHES:\s*([^\n]+)/);
    if (searchesMatch && searchesMatch[1]) {
      knowledgeData.search_queries_made = searchesMatch[1].split(',').map(s => s.trim()).filter(s => s);
    }

    // Remove the tracking block from the response
    cleanResponse = aiOutput.replace(/KNOWLEDGE_SOURCES_USED:.*?END_TRACKING/s, '').trim();

    console.log(`Extracted knowledge data: ${knowledgeData.knowledge_sources_used} sources used`);

  } else {
    console.log('No knowledge tracking found in AI response');
  }

} catch (error) {
  console.log('Error parsing knowledge tracking:', error.message);
  // Keep defaults and original response
}

// Return the processed data for the workflow
return {
  // Clean response for the customer (without tracking)
  output: cleanResponse,
  gmail_id: $('Get a message').first().json.id,
  thread_id: $('Get a message').first().json.threadId,
  execution_id: $('Generate execution ID').first().json.executionId,
  subject: $('Get a message').first().json.headers.subject,
  to: $('Get a message').first().json.from.value[0].address,

  // Knowledge tracking data for database
  knowledge_sources_used: knowledgeData.knowledge_sources?.length,
  knowledge_sources: knowledgeData.knowledge_sources,
  search_queries_made: knowledgeData.search_queries_made,

  // Additional metadata for tracking
  has_knowledge_sources: knowledgeData.knowledge_sources?.length,
  raw_ai_output: aiOutput // Keep original for debugging
};

